t();        printWindow.close();    }</script><script>    $(document).ready(function () {        $(".CatchReceipt").click(function () {            var dailymove = $(this).attr('CatchReceipt') ;                    $('.bd-example-modal-lg').modal('toggle');            $.ajax({                type: "GET",                url: '{{route('catch_receipts.details')}}/?id=' + dailymove,                success: function (data) {                    $('#model').empty()                    $('#model').append(data);                    $('.bd-example-modal-lg').modal('toggle');                }            });        });    });</script>